import {
  Component, OnInit, Input
} from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { AddCoursesComponent } from '../popups/add-courses/add-courses.component';

@Component({
  selector: 'app-side-nav',
  templateUrl: './side-nav.component.html',
  styleUrls: ['./side-nav.component.css']
})
export class SideNavComponent implements OnInit {

  @Input() sideNavStatus: boolean = false;
  list = [
    {
      number: '1',
      name: 'Add Courses',
    },
    {
      number: '2',
      name: 'Add Schedules'
    },
    {
      number: '3',
      name: 'View Courses'
    },
    {
      number: '4',
      name: 'View Schedules',
    },
    {
      number: '5',
      name: 'Registered Students',
    },
  ];

  constructor(private dialog: MatDialog) { }

  ngOnInit(): void {

  }

  openDialog(name) {
    if (name == "Add Courses") {
      this.dialog.open(AddCoursesComponent, {
        width: '250px',
        data: "right click"
      })
    }

  }
}
<div class="side-nav-content ">
    <ul class="nav-list" [ngClass]="{'nav-list-open':sideNavStatus}">
        <i class="fa-solid fa-caret-down fa-beat fa-xl"></i>
        <li class="nav-list-item" *ngFor="let item of list">
            <i></i>
            <span>
                <a  (click)="openDialog('{{item.name}}')">{{item.name}}</a>
            </span>
        </li>

    </ul>  
</div>
