import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { CourseService } from '../../services/course.service';

@Component({
  selector: 'app-add-courses',
  templateUrl: './add-courses.component.html',
  styleUrls: ['./add-courses.component.css']
})
export class AddCoursesComponent {
  constructor(private serv: CourseService, private dialog: MatDialog) { }

  courses = new FormGroup({
    name: new FormControl('', Validators.required),
    code: new FormControl('', Validators.required),
    pre: new FormControl(''),
    dept: new FormControl('', Validators.required)
  });

  get name() {
    return this.courses.get('name') as FormControl;
  }
  get code() {
    return this.courses.get('code') as FormControl;
  }
  get pre() {
    return this.courses.get('pre') as FormControl;
  }
  get dept() {
    return this.courses.get('dept') as FormControl;
  }

  addCourses() {
    this.serv.addCourse([
      this.courses.value.name,
      this.courses.value.code,
      this.courses.value.pre,
      this.courses.value.dept

    ]).subscribe(res => {
      if (res == "AlreadyExist") {
        alert("This course already exist !");
        this.dialog.closeAll();

      }
      else {
        alert("Successfully added !");
        this.dialog.closeAll();
      }
    });

  }
}
<form (ngSubmit)="addCourses()" [formGroup]="courses">
    <h1 mat-dialog-title>Add New Courses</h1>

    <div mat-dialog-content class="style">
        <mat-form-field>
            <mat-label>Course Name</mat-label>
            <input matInput placeholder="Ex. Nougat" formControlName="name">
        </mat-form-field>

        <mat-form-field>
            <mat-label>Course Code</mat-label>
            <input matInput placeholder="" formControlName="code">
        </mat-form-field>

        <mat-form-field>
            <mat-label>Select Department</mat-label>
            <mat-select formControlName="dept">
                <mat-option value="Biology" >Biology</mat-option>
                <mat-option value="Mathematics">Mathematics</mat-option>
                <mat-option value="Commputer science">Computer Science</mat-option>
            </mat-select>
        </mat-form-field>
        <mat-form-field>
            <mat-label>Pre-Requirements</mat-label>
            <textarea matInput formControlName="pre"></textarea>
        </mat-form-field>
        <div class="example-button-row">
            <button mat-raised-button type="submit">Add</button>
        </div>

    </div>

</form>
