import { Component } from '@angular/core';

@Component({
  selector: 'app-view-schedules',
  standalone: true,
  imports: [],
  templateUrl: './view-schedules.component.html',
  styleUrl: './view-schedules.component.css'
})
export class ViewSchedulesComponent {
  courses: any;
  sideNavStatus: boolean = false;
  displayedColumns: string[] = ['code', 'name', 'stime', 'etime','day','deadline','max','registered', 'delete', 'update'];
  constructor(private serv: CourseService) { }

  frm = new FormGroup({
    code: new FormControl(''),
    name: new FormControl(''),
    stime: new FormControl(''),
    etime: new FormControl(''),
    day: new FormControl(''),
    deadLine: new FormControl(''),
    max: new FormControl(''),
    registered: new FormControl('')


  });

  ngOnInit() {
    this.serv.getSchedules().subscribe((res) => {
      this.courses = res;
    });
  }

  delete(code: string) {
    if (confirm("Are you sure to delete " + code)) {
      this.serv.deleteSchedule(code).subscribe(res => {
        if (res == 'Success') {
          alert("Successfully deleted");
          this.ngOnInit();
        }
        else {
          alert("ERROR!!")
        }

      })

    }
  }

  onEdit(courses: any) {
    this.courses.forEach(e => {
      e.isEdit = false;
    });
    courses.isEdit = true;
  }

  save(schedules: any) {
    if (confirm("Are you sure to make changes ?")) {
      console.log(schedules.code);
      this.frm.value.code = courses.courseId;
      this.frm.value.name = courses.instructor;
      this.frm.value.stime = courses.sTIme;
      this.frm.value.etime = courses.eTIme;
      this.frm.value.deadLine = courses.deadLine;
      this.frm.value.max = courses.maxCapacity;
      this.frm.value.registered = courses.currentEnrollment;
      this.frm.value.day = courses.year;

      this.serv.updateSchedule([
        this.frm.value.name,
        this.frm.value.code,
        this.frm.value.stime,
        this.frm.value.etime,
        this.frm.value.deadLine,
        this.frm.value.max,
        this.frm.value.registered,
        this.frm.value.day 
      ]).subscribe(res => {
        if (res == 'Success') {
          alert("Successfully Updated !");
          courses.isEdit = false;
        }
        else {
          alert("Error!!");
          courses.isEdit = false;

        }
      });
    }
  }
  cancel(courses: any) {
    courses.isEdit = false;
  }

}
