import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { CourseService } from '../services/course.service';

@Component({
  selector: 'app-view-courses',

  templateUrl: './view-courses.component.html',
  styleUrl: './view-courses.component.css'
})
export class ViewCoursesComponent implements OnInit {
  courses: any;
  sideNavStatus: boolean = false;
  displayedColumns: string[] = ['code', 'name', 'dept', 'pre','delete','update'];
  constructor(private serv: CourseService) { }

  frm=new FormGroup({
    code: new FormControl(''),
    name: new FormControl(''),
    dept: new FormControl(''),
    pre: new FormControl('')
  })

  ngOnInit() {
    this.serv.getCourses().subscribe((res) => {
      this.courses = res;
      console.log(res);
    });
  }

  delete(code: string) {
    if (confirm("Are you sure to delete " + code)) {
      this.serv.deleteCourse(code).subscribe(res => {
        if (res == 'Success') {
          alert("Successfully deleted");
          this.ngOnInit();
        }
        else {
          alert("ERROR!!")
        }

      })

    }
  }

  update() {

  }

}
<div class="container-fluid g-0">
    <app-nav (sideNavToggled)="sideNavStatus= $event;"></app-nav>

    <main>
        <app-side-nav [sideNavStatus]="sideNavStatus" [ngClass]="{'app-side-nav-open':sideNavStatus}"></app-side-nav>
        <router-outlet></router-outlet>

    </main>
</div>
<body>


    <table mat-table class="mat-elevation-z8 demo-table" [dataSource]="courses">

        <ng-container matColumnDef="code">
            <th mat-header-cell *matHeaderCellDef>CODE</th>
            <td mat-cell *matCellDef="let courses">{{courses.code}}</td>
            <td matInput [(ngModel)]="courses.code"></td>
        </ng-container>

        <ng-container matColumnDef="name">
            <th mat-header-cell *matHeaderCellDef>COURSE NAME</th>
            <td mat-cell *matCellDef="let courses">{{courses.name}}</td>

        </ng-container>

        <ng-container matColumnDef="dept">
            <th mat-header-cell *matHeaderCellDef>DEPARTMENT</th>
            <td mat-cell *matCellDef="let courses">{{courses.department}}</td>

        </ng-container>

        <ng-container matColumnDef="pre">
            <th mat-header-cell *matHeaderCellDef>PRE_REQUIREMENTS</th>
            <td mat-cell *matCellDef="let courses;let i=index">
                {{courses.preRequirements}}

                <mat-form-field style="width: 70px;"
                                [appearance]="VOForm.get('VORows').value[i].isEditable? 'none' : 'legacy'">
                    <input matInput type="text" formControlName="symbol" [readonly]="VOForm.get('VORows').value[i].isEditable">
                </mat-form-field>

            </td>
        </ng-container>


        <ng-container matColumnDef="delete">
            <th mat-header-cell *matHeaderCellDef>DELETE</th>
            <td mat-cell *matCellDef="let courses"><a class="btn" href="javascript: delete(courses.code)" (click)="delete(courses.code)" ><i class="fa-solid fa-trash  fa-xl" style="color: #ff2600;"></i></a></td>

        </ng-container>
        <ng-container matColumnDef="update">
            <th mat-header-cell *matHeaderCellDef>UPDATE</th>
            <td mat-cell *matCellDef="let courses"><a  class="btn"><i class="fa-solid fa-pen-to-square fa-xl" style="color: #002e7a;"></i></a></td>

        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

        <ng-container>

        </ng-container>


    </table>


</body>


