import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { MatDialog } from '@angular/material/dialog';
import { CustomTimePipe } from '../../custom-time.pipe';
import { CourseService } from '../../services/course.service';
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-add-schedules',

  templateUrl: './add-schedules.component.html',
  styleUrl: './add-schedules.component.css'
})
export class AddSchedulesComponent {
  constructor(private serv: CourseService, private dialog: MatDialog) { }
  formattedTime: any;

  Schedules = new FormGroup({
    name: new FormControl('', Validators.required),
    code: new FormControl('', Validators.required),
    day: new FormControl('', Validators.required),
    sTime: new FormControl('', Validators.required),
    eTime: new FormControl('', Validators.required),
    capacity: new FormControl('', Validators.required),
    deadLine: new FormControl('', Validators.required),
  });

  get name() {
    return this.Schedules.get('name') as FormControl;
  }
  get code() {
    return this.Schedules.get('code') as FormControl;
  }
  get day() {
    return this.Schedules.get('day') as FormControl;
  }
  get sTime() {
    return this.Schedules.get('sTime') as FormControl;
  }
  get eTime() {
    return this.Schedules.get('eTime') as FormControl;
  }
  get capacity() {
    return this.Schedules.get('capacity') as FormControl;
  }
  get deadLine() {
    return this.Schedules.get('deadLine') as FormControl;
  }



  addSchedules() {
    this.Schedules.value.sTime = this.Schedules.value.sTime + ":00";
    this.Schedules.value.eTime = this.Schedules.value.eTime + ":00";
    this.Schedules.value.deadLine = this.Schedules.value.deadLine + ":00";

    console.log(this.Schedules.value.deadLine);


      this.serv.addSchedules([
      this.Schedules.value.name,
      this.Schedules.value.code,
      this.Schedules.value.day,
      this.Schedules.value.sTime,
      this.Schedules.value.eTime,
        this.Schedules.value.capacity,
        this.Schedules.value.deadLine,

    ]).subscribe(res => {
      if (res == "Success") {
        alert("Successfully added !");
        this.dialog.closeAll();
        if (window.location.href == "http://localhost:4200/signin/schedules") {
          window.location.reload();
        }
      }
      else {
        alert("Error!!");
        this.dialog.closeAll();
        if (window.location.href == "http://localhost:4200/signin/schedules") {
          window.location.reload();
        }

      }

    });

  }
}
<form [formGroup]="Schedules" (ngSubmit)="addSchedules()">

    <h1 mat-dialog-title>Add New Schedules</h1>

    <div mat-dialog-content class="style">

        <div class="row">
            <div class="col">
            <mat-form-field>
                <mat-label>Course Code</mat-label>
                <input matInput placeholder="" formControlName="code">
            </mat-form-field>
                </div>
        </div>

        <div class="row">
            <mat-form-field>
                <mat-label>Instructors Name </mat-label>
                <input matInput placeholder="" formControlName="name">
            </mat-form-field>
        </div>

        <div class="row">
            <div class="col">
                <mat-form-field>
                    <mat-label>Select Day</mat-label>
                    <mat-select formControlName="day">
                        <mat-option value="Monday">Monday</mat-option>
                        <mat-option value="Tuesday">Tuesday</mat-option>
                        <mat-option value="Wednesday">Wednesday</mat-option>
                        <mat-option value="Thursday">Thursday</mat-option>
                        <mat-option value="Friday">Friday</mat-option>
                        <mat-option value="Saturday">Saturday</mat-option>
                        <mat-option value="Sunday">Sunday</mat-option>
                    </mat-select>
                </mat-form-field>
            </div>

            <div class="col">
                <mat-form-field>
                    <input matInput type="datetime-local" placeholder="start date" formControlName="deadLine">
                </mat-form-field>
            </div>

        </div>

            <div class="row">
                <div class="col">

                    <mat-form-field>
                        <mat-label>Start Time</mat-label>
                        <input matInput
                               name="selected_time_A"
                               [ngxMatTimepicker]="pickerA"
                               placeholder="4:00 PM"
                               readonly formControlName="sTime" [format]="24" />
                        <mat-icon matSuffix
                                  (click)="pickerA.open()">
                        </mat-icon>
                    </mat-form-field>
                    <ngx-mat-timepicker #pickerA></ngx-mat-timepicker>
                </div>
                <div class="col">


                    <mat-form-field>
                        <mat-label>End Time</mat-label>
                        <input matInput
                               name="selected_time_B"
                               [ngxMatTimepicker]="pickerB"
                               placeholder="4:00 PM"
                               readonly formControlName="eTime" [format]="24" />
                        <mat-icon matSuffix
                                  (click)="pickerB.open()">
                        </mat-icon>
                    </mat-form-field>
                    <ngx-mat-timepicker #pickerB></ngx-mat-timepicker>
                </div>
            </div>





            <mat-form-field>
                <mat-label>Max Capacity</mat-label>
                <input matInput placeholder="" formControlName="capacity">
            </mat-form-field>




            <div class="example-button-row">
                <button mat-raised-button type="submit">Add</button>
            </div>

        </div>
</form>
