import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { BehaviorSubject } from 'rxjs';
import { AuthService } from '../services/auth.service';
import { ResetPasswordService } from '../services/reset-password.service';

@Component({
  selector: 'app-password-reset',
  templateUrl: './password-reset.component.html',
  styleUrl: './password-reset.component.css'
})
export class PasswordResetComponent {

  isUserValid: boolean = false;
  ress: any;
  resetPassword: string;
  isValid: boolean;

  ngOnInit() {

  }
  constructor(private authService: AuthService, private router: Router, private resetserv: ResetPasswordService) { }

  loginfrm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    pwd: new FormControl('', [Validators.required])
  });

  get email() {
    return this.loginfrm.get("email");
  }

  get pwd() {
    return this.loginfrm.get("pwd");
  }



  loginSubmit() {
    this.authService.loginStudent([
      this.loginfrm.value.email,
      this.loginfrm.value.pwd
    ]).subscribe(res => {
      if (res == "Failure") {
        this.isUserValid = false;
        alert("Login unsuccessful");

      }
      else {
        this.isUserValid = true;
        this.authService.setToken(res);
        this.ress = this.authService.loadCurrentUser();
        if (this.ress.role == "Admin") {
          this.router.navigateByUrl('/signin/courses');
        }
      }
    });

  }

  checkValidEmail(event: string) {
    const value = event;
    const pattern = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
    this.isValid = pattern.test(value);
    return this.isValid

  }

  confirm() {
    console.log(this.resetPassword);

    this.resetserv.sendResetpasswordLink(this.resetPassword)
      .subscribe({
        next: (res) => {
          alert("Success");
          this.resetPassword = "";
          const btn = document.getElementById("closebtn");
          btn.dispatchEvent(new Event('click'));
        },
        error: (err) => {
          alert("error");
        }
      });
  }
}


    <form class="border p-3 rounded-3 perfect-centering" [formGroup]="loginfrm" (ngSubmit)="loginSubmit()">
        <div class="row g-0">
            <div class="col text-center p-2 mb-3 bg-light">
                <span class="fs-3">Password Reset</span>
            </div>
        </div>
        <div class="row g-0">
            <div class="col mb-3">
                <label for="pwd" class="form=label">New Password</label>
                <input type="password" class="form-control" id="pwd" formControlName="pwd" />
                <div class="form-text text-danger">
                    Invalid Email
                </div>
            </div>
        </div>
        <div class="row g-0">
            <div class="col mb-3">
                <label for="rpwd" class="form=label">Confirm Password</label>
                <input type="password" class="form-control" id="rpwd" formControlName="rpwd" />
                <div class="form-text text-danger">
                    Password Required
                </div>
            </div>
        </div>



        <div class="row">
            <div class="col-6 offset-3">
                <div class="d-grid">
                    <button type="submit" class="btn btn-primary" [disabled]="loginfrm.invalid">Reset </button>
                </div>
            </div>
        </div>
    </form>
